import {createStore} from 'redux';
// import { connect } from 'react-redux'

const countReducer = (state = {counter: 0}, action) => {
    if(action.type = 'INC') {
        return {
            counter : state.counter + 1
        } 
    }
    if(action.type = 'DEC') {
        return {
            counter : state.counter - 1
        } 
    }

    return state;
}

const store = createStore(countReducer);

export default store;
